import React, { useState } from 'react';
import { ethers } from 'ethers';
import GuessGame from './GuessGame.json';
import zamaLogo from './assets/zamaLogo.jpg';
import zamaBlock from './assets/zamaBlock.png';

function App() {
  const [guess, setGuess] = useState('');
  const [message, setMessage] = useState('');

  async function submitGuess() {
    if (!window.ethereum) return;
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const addr = process.env.REACT_APP_CONTRACT_ADDRESS;
    const contract = new ethers.Contract(addr, GuessGame.abi, signer);
    // For demo, just hex-encode guess
    const hexGuess = Buffer.from(guess).toString('hex');
    const tx = await contract.submitEncryptedGuess("0x"+hexGuess);
    await tx.wait();
    setMessage("Guess submitted!");
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-yellow-400 font-sans">

      {/* Top Section */}
      <header className="w-full text-center p-6">
        <h1 className="text-4xl font-extrabold text-black mb-2">üîê Zama FHE Guess Game</h1>
        <p className="text-lg text-black">
          Zama is pioneering Fully Homomorphic Encryption (FHE) technology, enabling smart contracts
          to compute on encrypted data while preserving privacy. This demo showcases a simple guessing game
          powered by FHEVM.
        </p>
      </header>

      {/* Logo Images */}
      <div className="flex gap-10 justify-center items-center my-6">
        <img src={zamaLogo} alt="Zama Logo" className="w-32 h-32 object-contain rounded-xl shadow-lg" />
        <img src={zamaBlock} alt="Zama Block" className="w-40 h-40 object-contain rounded-xl shadow-lg" />
      </div>

      {/* Guess Input */}
      <div className="bg-white rounded-2xl shadow-lg p-10 w-96 flex flex-col items-center">
        <h2 className="text-2xl font-bold text-black mb-4">Enter Your Guess</h2>
        <input
          className="w-full p-3 border-2 border-black rounded-xl mb-4 text-center focus:outline-none focus:ring-4 focus:ring-black"
          placeholder="Enter a number..."
          value={guess}
          onChange={(e) => setGuess(e.target.value)}
        />
        <button
          onClick={submitGuess}
          className="bg-black text-white px-6 py-3 rounded-xl font-semibold hover:bg-gray-800 transition"
        >
          Submit Guess
        </button>
        {message && <p className="text-green-700 mt-4 font-medium">{message}</p>}
      </div>
    </div>
  );
}

export default App;
